package com.rongji.egov.test;

import com.alibaba.fastjson.JSON;
import com.rongji.egov.datasource.DataSourceHandler;
import com.rongji.egov.mybatis.base.builder.SQLWrapper;
import com.rongji.egov.mybatis.base.builder.assistant.Builder;
import com.rongji.egov.mybatis.base.builder.assistant.LambdaHelper;
import com.rongji.egov.mybatis.base.mapper.BaseMapper;
import com.rongji.egov.mybatis.base.plugin.Page;
import com.rongji.egov.mybatis.base.querier.SelectPageQuerier;
import com.rongji.egov.mybatis.base.querier.UpdateQuerier;
import com.rongji.egov.mybatis.base.sql.SQLCriteria;
import com.rongji.egov.mybatis.base.sql.SQLSelector;
import com.rongji.egov.mybatis.base.sql.SQLUpdater;
import com.rongji.egov.test.model.DocOpinion;
import org.junit.Test;
import org.junit.runner.RunWith;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import org.springframework.boot.test.context.SpringBootTest;
import org.springframework.test.context.ActiveProfiles;
import org.springframework.test.context.junit4.SpringRunner;

import javax.annotation.Resource;
import java.util.stream.Collectors;

@SpringBootTest
@RunWith(SpringRunner.class)
@ActiveProfiles("migration_rms_resource")
public class TestDocOpinion {
    @Resource
    BaseMapper baseMapper;
    private static final Logger LOGGER = LoggerFactory.getLogger(TestDocOpinion.class);

    private static final SQLCriteria criteria = new SQLCriteria(
                    LambdaHelper.fieldName(DocOpinion::getDocId) + " = ?",
                    "1543a695c54b0000"
            );
    public SQLSelector selector() {
        return Builder.register(SQLSelector::new)
                .set(SQLSelector::setModel, DocOpinion.class)
                .set(SQLSelector::setFields, SQLWrapper.getSqlFields(DocOpinion.class))
                .set(SQLSelector::setWhere, criteria)
                .set(SQLSelector::setLimit, 0, 5)
                .build();
    }

    @Test
    public void query() {
        DataSourceHandler.set("XCOA");
        SQLSelector selector = selector();
        Page<DocOpinion> result = baseMapper.select(new SelectPageQuerier<DocOpinion>().setResultMap(DocOpinion.class).setSqlHandler(selector));
        System.out.println(Thread.currentThread().getName() + ": " + JSON.toJSONString(result));
        LOGGER.debug("=== total: {} ===", result.getTotal());
        LOGGER.debug("======holders : {} =======", DataSourceHandler.targetDataSourceHolders().stream().map(String::valueOf).collect(Collectors.joining(", ")));

        DocOpinion opinion = result.getList().get(0);
        opinion.setOpinionUserNameImage("data:image/png;base64,");
        int r = baseMapper.update(new UpdateQuerier().setSqlHandler(new SQLUpdater(DocOpinion.class, opinion, new SQLCriteria("id = ?", opinion.getId()))));
        LOGGER.debug("=== update rows : {} ===", r);
    }
}
